"use client";

import { useEffect } from "react";

export default function ServiceWorkerRegister() {
  useEffect(() => {
    if (!("serviceWorker" in navigator)) return;
    const onLoad = () => {
      navigator.serviceWorker.register("/sw.js").catch((e) => {
        // älä kaada UI:ta
        console.error("[sw register]", e);
      });
    };
    window.addEventListener("load", onLoad);
    return () => window.removeEventListener("load", onLoad);
  }, []);

  return null;
}

// dateutils.ts
export type WeekStartDay = "monday" | "sunday";

// Paikallinen keskiyö -> YYYY-MM-DD ilman UTC-heittelyä
export function toLocalISO(date: Date) {
  const d = new Date(date);
  d.setHours(0, 0, 0, 0);
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd}`;
}

export function addDaysLocalISO(iso: string, add: number) {
  const [y, m, da] = iso.split("-").map(Number);
  const d = new Date(y, m - 1, da); // paikallinen
  d.setDate(d.getDate() + add);
  return toLocalISO(d);
}

export function alignToWeekStart(iso: string, weekStart: WeekStartDay) {
  const [y, m, da] = iso.split("-").map(Number);
  const d = new Date(y, m - 1, da); // paikallinen
  const day = d.getDay(); // 0 = su, 1 = ma
  const startIndex = weekStart === "monday" ? 1 : 0;
  const diff = (day - startIndex + 7) % 7;
  d.setDate(d.getDate() - diff);
  return toLocalISO(d);
}

// src/lib/pushClient.ts
import { supabase } from "@/lib/supaBaseClient";

const PUBLIC_VAPID = process.env.NEXT_PUBLIC_VAPID_PUBLIC_KEY || "";

function urlBase64ToUint8Array(base64String: string) {
  const padding = "=".repeat((4 - (base64String.length % 4)) % 4);
  const base64 = (base64String + padding).replace(/-/g, "+").replace(/_/g, "/");
  const raw = atob(base64);
  const output = new Uint8Array(raw.length);
  for (let i = 0; i < raw.length; ++i) output[i] = raw.charCodeAt(i);
  return output;
}

export async function ensureServiceWorker() {
  if (!("serviceWorker" in navigator)) throw new Error("Service Worker ei tuettu");
  const reg = await navigator.serviceWorker.register("/sw.js");
  await navigator.serviceWorker.ready;
  return reg;
}

export async function requestPermissionAndSubscribe(): Promise<boolean> {
  if (!PUBLIC_VAPID) { console.error("NEXT_PUBLIC_VAPID_PUBLIC_KEY puuttuu"); return false; }

  const perm = await Notification.requestPermission();
  if (perm !== "granted") return false;

  const reg = await ensureServiceWorker();
  const sub = await reg.pushManager.subscribe({
    userVisibleOnly: true,
    applicationServerKey: urlBase64ToUint8Array(PUBLIC_VAPID),
  });

  // talleta supabaseen
const json: PushSubscriptionJSON = sub.toJSON();
const endpoint: string = json.endpoint!;
const p256dh: string | undefined = json.keys?.p256dh;
const auth: string | undefined = json.keys?.auth;

const { error } = await supabase
  .from("push_subscriptions")
  .upsert({ endpoint, p256dh, auth, is_active: true }, { onConflict: "endpoint" });

if (error) {
  console.error(error);
  return false;
}
return true;

}

export type SendEmailPayload = {
  to: string | string[];
  subject: string;
  text?: string;
  html?: string;
  replyTo?: string;
};

export async function sendEmail(payload: SendEmailPayload) {
  const res = await fetch("/api/sendEmail", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(payload),
  });
  const data = await res.json().catch(() => ({}));
  if (!res.ok) {
    throw new Error(`Email send failed (${res.status}) ${JSON.stringify(data)}`);
  }
  return data;
}

// src/lib/settingsDb.ts
import type { Settings } from "@/lib/settingsSchema";


 function resolveFunctionsBase(): string {
   const direct = process.env.[REDACTED]?.trim();
   if (direct) return direct.replace(/\/+$/, "");
   const supaUrl = process.env.NEXT_PUBLIC_SUPABASE_URL?.trim();
   if (!supaUrl) throw new Error("Missing NEXT_PUBLIC_SUPABASE_URL");
   if (supaUrl.includes("localhost") || supaUrl.includes("127.0.0.1")) {
     if (typeof window !== "undefined") {
       const h = window.location.hostname;
       if (h && h !== "localhost" && h !== "127.0.0.1") {
         return `http://${h}:54321/functions/v1`;
       }
     }
     return "http://127.0.0.1:54321/functions/v1";
   }
   return supaUrl.replace(".supabase.co", ".functions.supabase.co");
 }
 const FN_URL = `${resolveFunctionsBase()}/app_settings`;

 async function getAuthToken(): Promise<string | undefined> {
   // Next.js clientissä: hae token supabasen kautta
   try {
     const { createClient } = await import("@supabase/supabase-js");
     const supabase = createClient(
       process.env.NEXT_PUBLIC_SUPABASE_URL!,
       process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY!
     );
     const { data: { session } } = await supabase.auth.getSession();
     return session?.access_token;
   } catch {
     return undefined;
   }
 }

export async function saveNotificationSettingsToDb(notifs: Settings["notifications"]) {
  if (typeof window !== "undefined") console.log("[app_settings] FN_URL:", FN_URL);
   const token = await getAuthToken();
   const anon = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;
   const res = await fetch(FN_URL, {
     method: "PUT",
     mode: "cors",
     headers: {
       "content-type": "application/json",
       ...(token || anon ? { Authorization: `Bearer ${token ?? anon}` } : {}),
     },
     body: JSON.stringify({ notifications: notifs }),
   });
   if (!res.ok) {
     const j = await res.json().catch(() => ({}));
     throw new Error(j?.error ?? `Failed to save settings (${res.status})`);
   }
}
 export async function loadNotificationSettingsFromDb():
   Promise<Settings["notifications"]> {
   const token = await getAuthToken();
   const anon = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY;
   const res = await fetch(FN_URL, {
     method: "GET",
     headers: { ...(token || anon ? { Authorization: `Bearer ${token ?? anon}` } : {}) },
   });
   if (!res.ok) {
     const j = await res.json().catch(() => ({}));
     throw new Error(j?.error ?? `Failed to load settings (${res.status})`);
   }
   const j = await res.json();
   return j.notifications as Settings["notifications"];
 }

// lib/settingsSchema.ts
import { z } from "zod";

export const themeValues = ["light", "dark", "system"] as const;
export type Theme = typeof themeValues[number];

export const languageValues = ["fi", "en", "sv"] as const;
export type Language = typeof languageValues[number];

export const weekStartValues = ["monday", "sunday"] as const;
export type WeekStartDay = typeof weekStartValues[number];

export const timeFormatValues = ["24h", "12h"] as const;
export type TimeFormat = typeof timeFormatValues[number];

export const dateFormatValues = ["dd.mm.yyyy", "mm/dd/yyyy", "yyyy-mm-dd"] as const;
export type DateFormat = typeof dateFormatValues[number];

export type TimePeriod = 7 | 10 | 14 | 30;

export const GeneralSettingsSchema = z.object({
  theme: z.enum(themeValues),
  language: z.enum(languageValues),
  weekStartDay: z.enum(weekStartValues),
  timeFormat: z.enum(timeFormatValues),
  dateFormat: z.enum(dateFormatValues),
  defaultTimePeriod: z.union([z.literal(7), z.literal(10), z.literal(14), z.literal(30)]),
  workingHours: z.object({
    start: z.string().regex(/^\d{2}:\d{2}$/),
    end: z.string().regex(/^\d{2}:\d{2}$/),
    breakDuration: z.number().int().min(0).max(240),
  }),
  autoSave: z.boolean(),
  autoSaveInterval: z.number().int().min(10).max(300),
});

export const AutoGenSettingsSchema = z.object({
  defaultHours: z.number().min(1).max(12),
  distributeEvenly: z.boolean(),
  respectWorkingHours: z.boolean(),
  skipWeekends: z.boolean(),
  skipHolidays: z.boolean(),
  maxConsecutiveDays: z.number().int().min(1).max(14),
  minRestHours: z.number().int().min(8).max(24),
});

export const ExportSettingsSchema = z.object({
  companyName: z.string().max(120),
  includeNames: z.boolean(),
  includeEmails: z.boolean(),
  includeDepartments: z.boolean(),
  includeInactiveEmployees: z.boolean(),
  includeEmptyShifts: z.boolean(),
  includeHourTotals: z.boolean(),
});

export const NotificationSettingsSchema = z.object({
  emailNotifications: z.boolean(),
  adminNotificationEmails: z.array(z.string().email()).max(50).default([]),
  absenceRequests: z.boolean(),
  scheduleChanges: z.boolean(),
  employeeUpdates: z.boolean(),
  systemUpdates: z.boolean(),
  dailyDigest: z.boolean(),
  digestTime: z.string().regex(/^\d{2}:\d{2}$/),
});

export const SystemSettingsSchema = z.object({
  version: z.string(),
  lastBackup: z.string().nullable().optional(),
  maintenanceMode: z.boolean(),
  debugMode: z.boolean(),
  maxEmployees: z.number().int().min(1).max(1000),
  dataRetentionDays: z.number().int().min(30).max(3650),
});

export const SettingsSchema = z.object({
  general: GeneralSettingsSchema,
  autoGeneration: AutoGenSettingsSchema,
  export: ExportSettingsSchema,
  notifications: NotificationSettingsSchema,
  system: SystemSettingsSchema,
});

export type Settings = z.infer<typeof SettingsSchema>;

