"use client";

import { create } from "zustand";
import { devtools } from "zustand/middleware";
import { toast } from "sonner";
import { supabase } from "@/lib/supaBaseClient";
import { persist, createJSONStorage } from "zustand/middleware";



// KÄYTÄ YHTÄ TOTUUTTA: ota tyypit yhdestä paikasta
import type { Employee, DateInfo } from "@/app/types";

// Sama DateCell kuin muualla
export type DateCell = DateInfo & { iso: string };

// Yhden solun persistomuoto
export type ShiftRow = {
  employee_id: string;
  work_date: string; // YYYY-MM-DD
  type: "normal" | "locked" | "absent" | "holiday";
  hours: number | null; // null sallitaan, mutta tallennetaan 0:ksi kun kirjoitetaan DB:hen
};
// Suodattimien tyyppi
export type Filters = {
  departments: string[];
  showActive: boolean;
  showInactive: boolean;
  searchTerm: string;
};

// Sisäinen muutos, jota kerätään saveAll:lle
type PendingChange = {
  employee_id: string;
  work_date: string;
  hours: number; // 0 => poista, >0 => upsert "normal"
};

type State = {

  // Hydratoitu perusdata
  employees: Employee[];
  dates: DateCell[];

  // Vuorot mapattuna
  shiftsMap: Record<string, ShiftRow>;

  // Muutokset jotka pitää tallentaa
  pending: Record<string, PendingChange>;

  // Undo/redo pino
  undoStack: PendingChange[];
  redoStack: PendingChange[];

  // UI-signaalit
  dirty: boolean;

  // Filtterit
  filters: Filters;
  setFilters: (patch: Partial<Filters>) => void;
  resetFilters: () => void;

  startDateISO: string;
  days: 7 | 10 | 14 | 30;

  setRange: (startISO: string, days: State["days"]) => void;
  setStartDate: (startDateISO: string) => void;
  shiftRange: (deltaDays: number) => void;
  hasHydrated: boolean;
  _setHydrated: (v: boolean) => void;

  // Toiminnot
  hydrate: (payload: {
    employees: Employee[];
    dates: DateCell[];
    shifts: ShiftRow[];
  }) => void;

  applyCellChange: (p: { employee_id: string; work_date: string; hours: number | null }) => void;

  saveAll: () => Promise<void>;

  undo: () => void;
  redo: () => void;
};

function keyOf(empId: string, iso: string) {
  return `${empId}|${iso}`;
}

function todayLocalISO() {
  const d = new Date();
  d.setHours(0, 0, 0, 0);
  const yyyy = d.getFullYear();
  const mm = String(d.getMonth() + 1).padStart(2, "0");
  const dd = String(d.getDate()).padStart(2, "0");
  return `${yyyy}-${mm}-${dd}`;
}

export const useScheduleStore = create<State>()(
  persist(
  devtools((set, get) => ({
    employees: [],
    dates: [],
    shiftsMap: {},
    pending: {},
    undoStack: [],
    redoStack: [],
    dirty: false,
    hasHydrated: false,
    _setHydrated: (v) => set({ hasHydrated: v }),
    
     // ---Filtterit (init + setterit juureen)---
     filters: { departments: [], showActive: false, showInactive: false, searchTerm: "" },
     setFilters: (partial) =>
       set((state) => ({ filters: { ...state.filters, ...partial } })),
     resetFilters: () =>
       set({ filters: { departments: [], showActive: false, showInactive: false, searchTerm: "" } }),

startDateISO: todayLocalISO(),
days: 10 as State["days"],

    hydrate: ({ employees, dates, shifts }) => {
      // Rakennetaan map shifteistä
      const map: Record<string, ShiftRow> = {};
      for (const s of shifts) {
        map[keyOf(s.employee_id, s.work_date)] = {
          ...s,
          hours: s.hours ?? 0,
          // Varmista että type on unionista (tai normal jos tuntematon)
          type:
            s.type === "normal" ||
            s.type === "locked" ||
            s.type === "absent" ||
            s.type === "holiday"
              ? s.type
              : "normal",
        };
      }
      set({
        employees,
        dates,
        shiftsMap: map,
        pending: {},
        undoStack: [],
        redoStack: [],
        dirty: false,
      });
    },

    applyCellChange: ({ employee_id, work_date, hours }) => {
      const h = typeof hours === "number" ? hours : 0;
      const k = keyOf(employee_id, work_date);
      const { shiftsMap, pending, undoStack } = get();

      // Laske edellinen arvo (käytetään undo:ssa)
      const prev = shiftsMap[k];

      // Päivitä live-näkymään:
      const nextMap = { ...shiftsMap };
      if (h <= 0) {
        // 0h => poista vuoro näkyvistä
        delete nextMap[k];
      } else {
        // >0h => laita normal-h vuoro
        nextMap[k] = {
          employee_id,
          work_date,
          type: "normal",
          hours: h,
        };
      }

      // Päivitä pending: 0h => merkkaa poistoksi, muuten upsertiksi
      const nextPending = { ...pending, [k]: { employee_id, work_date, hours: h } };

      set({
        shiftsMap: nextMap,
        pending: nextPending,
        undoStack: [...undoStack, { employee_id, work_date, hours: prev?.hours ?? 0 }],
        redoStack: [],
        dirty: true,
      });
    },

setRange: (startISO, days) => set({ startDateISO: startISO, days }),
setStartDate: (startDateISO: string) => set({ startDateISO }),
shiftRange: (deltaDays: number) => {
  const { startDateISO, days } = get();
  const d = new Date(startDateISO + "T00:00:00Z");
  d.setUTCDate(d.getUTCDate() + deltaDays);
  set({ startDateISO: d.toISOString().slice(0, 10), days });
},

saveAll: async () => {
  const { pending } = get();
  const changes = Object.values(pending);
  if (!changes.length) {
    toast.info("Ei tallennettavia muutoksia");
    return;
  }

  const upserts: ShiftRow[] = [];
  const deletes: { employee_id: string; work_date: string }[] = [];

  for (const c of changes) {
    if (c.hours <= 0) {
      deletes.push({ employee_id: c.employee_id, work_date: c.work_date });
    } else {
      upserts.push({
        employee_id: c.employee_id,
        work_date: c.work_date,
        type: "normal",
        hours: c.hours,
      });
    }
  }

  try {
    // 1) Upsertit ensin (turvallista ja idempotenttia)
    if (upserts.length) {
      const { error } = await supabase
        .from("shifts")
        .upsert(upserts, { onConflict: "employee_id,work_date" });
      if (error) throw error;
    }

    // 2) Poistot **ilman ristiin-IN-bugia**: ryhmittele työntekijöittäin
    if (deletes.length) {
      const byEmp = new Map<string, string[]>();
      for (const d of deletes) {
        const arr = byEmp.get(d.employee_id) ?? [];
        arr.push(d.work_date);
        byEmp.set(d.employee_id, arr);
      }

      // Chunkkaa päivämääriä per employee_id, jotta IN-listat eivät kasva liikaa
      for (const [empId, dates] of byEmp.entries()) {
        const chunkSize = 1000;
        for (let i = 0; i < dates.length; i += chunkSize) {
          const sub = dates.slice(i, i + chunkSize);
          const { error } = await supabase
            .from("shifts")
            .delete()
            .eq("employee_id", empId) // kriittinen: kohdistus yhteen employeeen
            .in("work_date", sub);    // vain kyseisen employee_id:n päivät
          if (error) throw error;
        }
      }
    }

    set({ pending: {}, dirty: false });
    toast.success("Tallennettu");
  } catch (e) {
    console.error(e);
    toast.error("Tallennus epäonnistui");
    // Älä nollaa pendingiä epäonnistumisessa
  }
},


    undo: () => {
      const { undoStack, shiftsMap, pending, redoStack } = get();
      if (!undoStack.length) return;
      const last = undoStack[undoStack.length - 1];

      const k = keyOf(last.employee_id, last.work_date);
      const current = shiftsMap[k]; // mitä on nyt UI:ssa

      // Palauta entinen tuntimäärä
      const nextMap = { ...shiftsMap };
      if (!last.hours || last.hours <= 0) {
        delete nextMap[k];
      } else {
        nextMap[k] = {
          employee_id: last.employee_id,
          work_date: last.work_date,
          type: "normal",
          hours: last.hours,
        };
      }

      // Päivitä pending vastaamaan undo-tilaa
      const nextPending = { ...pending, [k]: { employee_id: last.employee_id, work_date: last.work_date, hours: last.hours ?? 0 } };

      // Siirrä nykyinen tila redo-pinon itemiksi
      const redoItem: PendingChange = {
        employee_id: last.employee_id,
        work_date: last.work_date,
        hours: current?.hours ?? 0,
      };

      set({
        shiftsMap: nextMap,
        pending: nextPending,
        undoStack: undoStack.slice(0, -1),
        redoStack: [...redoStack, redoItem],
        dirty: true,
      });
    },

    redo: () => {
      const { redoStack, shiftsMap, pending, undoStack } = get();
      if (!redoStack.length) return;
      const next = redoStack[redoStack.length - 1];

      const k = keyOf(next.employee_id, next.work_date);
      const prev = shiftsMap[k];

      const nextMap = { ...shiftsMap };
      if (!next.hours || next.hours <= 0) {
        delete nextMap[k];
      } else {
        nextMap[k] = {
          employee_id: next.employee_id,
          work_date: next.work_date,
          type: "normal",
          hours: next.hours,
        };
      }

      const nextPending = { ...pending, [k]: { ...next } };

      set({
        shiftsMap: nextMap,
        pending: nextPending,
        redoStack: redoStack.slice(0, -1),
        undoStack: [...undoStack, { employee_id: next.employee_id, work_date: next.work_date, hours: prev?.hours ?? 0 }],
        dirty: true,
      });
    },
  })),
    {
      name: "schedule-ui", // avain localStorageen
      version: 1,
      storage:
        typeof window !== "undefined"
          ? createJSONStorage(() => localStorage)
          : undefined,
      // persistoi vain nämä (ei esim. shiftsMap tms.)
      partialize: (state) => ({
        startDateISO: state.startDateISO,
        days: state.days,
      }),
      // Kun persist-hydraus valmistuu -> merkitse valmiiksi
      onRehydrateStorage: () => (_state, error) => {
        if (error) {
          console.error("schedule-ui rehydrate failed", error);
          return;
        }
        // Ei 'set' scope:ssa -> kutsu action store-instanssin kautta
        _state?._setHydrated?.(true);
      },
  }
  )
);

// stores/useSettingsStore.ts
import { create } from "zustand";
import { persist, subscribeWithSelector } from "zustand/middleware";
import {
  Settings,
  SettingsSchema,
  DEFAULT_SETTINGS,
  Theme,
} from "@/lib/settingsSchema";

type State = {
  settings: Settings;

  setAll: (s: Settings) => void;
  reset: () => void;

  updateGeneral: <K extends keyof Settings["general"]>(
    key: K,
    value: Settings["general"][K]
  ) => void;

  updateAutoGen: <K extends keyof Settings["autoGeneration"]>(
    key: K,
    value: Settings["autoGeneration"][K]
  ) => void;

  updateExport: <K extends keyof Settings["export"]>(
    key: K,
    value: Settings["export"][K]
  ) => void;

  updateNotifications: <K extends keyof Settings["notifications"]>(
    key: K,
    value: Settings["notifications"][K]
  ) => void;

  updateSystem: <K extends keyof Settings["system"]>(
    key: K,
    value: Settings["system"][K]
  ) => void;

  importFromJson: (raw: unknown) =>
    | { ok: true }
    | { ok: false; error: string };

  exportToFile: () => void;
  hasHydrated: boolean;
  _setHydrated: (v: boolean) => void;
};

// ✅ OIKEA KOKOONPANO: persist(subscribeWithSelector(config), options)
export const useSettingsStore = create<State>()(
  persist(
    subscribeWithSelector<State>((set, get) => ({
      settings: DEFAULT_SETTINGS,

      setAll: (s) => set({ settings: s }),
      reset: () => set({ settings: DEFAULT_SETTINGS }),

      updateGeneral: (key, value) =>
        set((st) => ({
          settings: {
            ...st.settings,
            general: { ...st.settings.general, [key]: value },
          },
        })),

      updateAutoGen: (key, value) =>
        set((st) => ({
          settings: {
            ...st.settings,
            autoGeneration: {
              ...st.settings.autoGeneration,
              [key]: value,
            },
          },
        })),

      updateExport: (key, value) =>
        set((st) => ({
          settings: {
            ...st.settings,
            export: { ...st.settings.export, [key]: value },
          },
        })),

      updateNotifications: (key, value) =>
        set((st) => ({
          settings: {
            ...st.settings,
            notifications: {
              ...st.settings.notifications,
              [key]: value,
            },
          },
        })),

      updateSystem: (key, value) =>
        set((st) => ({
          settings: {
            ...st.settings,
            system: { ...st.settings.system, [key]: value },
          },
        })),

      importFromJson: (raw) => {
        let data: unknown = raw;
        if (typeof raw === "string") {
          try {
            data = JSON.parse(raw);
          } catch {
            return {
              ok: false as const,
              error: "Virheellinen JSON: ei voitu jäsentää.",
            };
          }
        }

        const parsed = SettingsSchema.safeParse(data);
        if (!parsed.success) {
          const msg = parsed.error.issues
            .map((i) => `${i.path.join(".") || "(root)"}: ${i.message}`)
            .join("; ");
          return { ok: false as const, error: msg };
        }

        set({ settings: parsed.data });
        return { ok: true as const };
      },

      exportToFile: () => {
        const data = JSON.stringify(get().settings, null, 2);
        const blob = new Blob([data], { type: "application/json" });
        const url = URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "soili-settings.json";
        document.body.appendChild(a);
        a.click();
        a.remove();
        URL.revokeObjectURL(url);
      },

      hasHydrated: false,
      _setHydrated: (v) => set({ hasHydrated: v }),

    })),
    {
      name: "soili-settings-v2",
          version: 2,
migrate: (persisted: unknown): State => {
  try {
    // jos dataa ei ole → palauta default state
    if (!persisted || typeof persisted !== "object") {
      return { settings: DEFAULT_SETTINGS, hasHydrated: false, _setHydrated: () => {}, setAll: () => {}, reset: () => {}, updateGeneral: () => {}, updateAutoGen: () => {}, updateExport: () => {}, updateNotifications: () => {}, updateSystem: () => {}, importFromJson: () => ({ ok: false, error: "not implemented" }), exportToFile: () => {} } as State;
    }

    const st = persisted as State;
    if (!st?.settings) {
      return { ...st, settings: DEFAULT_SETTINGS };
    }

    const prevNotifs: Partial<Settings["notifications"]> | undefined =
      st.settings.notifications;

    const nextNotifs: Settings["notifications"] = {
      ...prevNotifs,
      adminNotificationEmails: Array.isArray(
        prevNotifs?.adminNotificationEmails
      )
        ? prevNotifs!.adminNotificationEmails!
        : [],
      emailNotifications:
        prevNotifs?.emailNotifications ?? DEFAULT_SETTINGS.notifications.emailNotifications,
      absenceRequests:
        prevNotifs?.absenceRequests ?? DEFAULT_SETTINGS.notifications.absenceRequests,
      scheduleChanges:
        prevNotifs?.scheduleChanges ?? DEFAULT_SETTINGS.notifications.scheduleChanges,
      employeeUpdates:
        prevNotifs?.employeeUpdates ?? DEFAULT_SETTINGS.notifications.employeeUpdates,
      systemUpdates:
        prevNotifs?.systemUpdates ?? DEFAULT_SETTINGS.notifications.systemUpdates,
      dailyDigest:
        prevNotifs?.dailyDigest ?? DEFAULT_SETTINGS.notifications.dailyDigest,
      digestTime:
        prevNotifs?.digestTime ?? DEFAULT_SETTINGS.notifications.digestTime,
    };

    return {
      ...st,
      settings: {
        ...st.settings,
        notifications: nextNotifs,
      },
    };
  } catch {
    // fallback jos jotain hajoaa → default state
    return { settings: DEFAULT_SETTINGS } as State;
  }
},


      onRehydrateStorage: () => (_state, error) => {
        if (error) {
          console.error("settings-store rehydrate failed", error);
          return;
        }
        _state?._setHydrated?.(true);
      },
    }
  )
);

// (valinnainen) teema- ja push-sivuvaikutukset:
if (typeof window !== "undefined") {
  const applyTheme = (theme: Theme) => {
    const root = document.documentElement;
    const prefersDark =
      window.matchMedia?.("(prefers-color-scheme: dark)").matches;
    const eff = theme === "system" ? (prefersDark ? "dark" : "light") : theme;
    root.classList.remove("light", "dark");
    root.classList.add(eff);
  };

  applyTheme(useSettingsStore.getState().settings.general.theme);

  useSettingsStore.subscribe(
    (s) => s.settings.general.theme,
    (theme) => applyTheme(theme)
  );
}

// Apukoukku jos haluat kytkeä autosaven muualle
export function useAutoSaveConfig() {
  return useSettingsStore((s) => ({
    enabled: s.settings.general.autoSave,
    intervalSec: s.settings.general.autoSaveInterval,
  }));
}

"use client";

import React, { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Avatar, AvatarFallback } from './ui/avatar';
import { Textarea } from './ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from './ui/dialog';
import { 
  Clock, 
  CheckCircle, 
  XCircle, 
  Calendar,
  MessageSquare,
  User,
  AlertTriangle
} from 'lucide-react';
import { AbsenceRequest } from '../types';
import { toast } from 'sonner';
import { supabase } from '@/lib/supaBaseClient';
import { notifyAbsenceDecision } from '@/features/absences/notify';
import { useSettingsStore } from "@/store/useSettingsStore";

const AbsenceControlPanel = () => {
  const [requests, setRequests] = useState<AbsenceRequest[]>([]);
  const [adminResponse, setAdminResponse] = useState('');

  const emailEnabled =
  useSettingsStore((s) => s.settings?.notifications?.emailNotifications ?? true);

  // --- FETCH FROM SUPABASE ---
  useEffect(() => {
    const fetchAbsences = async () => {
const { data, error } = await supabase
  .from('absences')
  .select(`
    id,
    employee_id,
    start_date,
    end_date,
    reason,
    message,
    status,
    submitted_at,
    employees:employees!absences_employee_id_fkey ( name )
  `)
  .order('submitted_at', { ascending: false });

if (error) {
  console.error(error);
  toast.error('Poissaolojen haku epäonnistui');
  return;
}

type AbsenceRow = {
  id: string;
  employee_id: string;
  start_date: string;
  end_date: string | null;
  reason: string | null;
  message: string | null;
  status: 'pending' | 'approved' | 'declined';
  submitted_at: string;
  employees?: { name: string }[] | { name: string } | null;
};

const mapped: AbsenceRequest[] = (data as AbsenceRow[]).map((r) => {
  const employeeName = Array.isArray(r.employees)
    ? r.employees[0]?.name
    : r.employees?.name;

  return {
    id: r.id,
    employeeId: r.employee_id,
    employeeName: employeeName ?? 'Tuntematon',
    startDate: r.start_date,
    endDate: r.end_date ?? '',
    reason: r.reason ?? '',
    status: r.status,
    submittedAt: r.submitted_at,
    message: r.message ?? '',
  };
});


      setRequests(mapped);
      
    };

    fetchAbsences();
  }, []);

  // --- STATUS HELPERS (UI) ---
  const getStatusBadge = (status: AbsenceRequest['status']) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline" className="bg-amber-50 text-amber-700 border-amber-300"><Clock className="w-3 h-3 mr-1" />Odottaa</Badge>;
      case 'approved':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-300"><CheckCircle className="w-3 h-3 mr-1" />Hyväksytty</Badge>;
      case 'declined':
        return <Badge variant="outline" className="bg-red-50 text-red-700 border-red-300"><XCircle className="w-3 h-3 mr-1" />Hylätty</Badge>;
    }
  };

  // --- SUPABASE UPDATES ---
const handleApprove = async (requestId: string) => {
  // Optimistinen päivitys
  setRequests(prev => prev.map(r => r.id === requestId ? { ...r, status: 'approved' } : r));

  const target = requests.find(r => r.id === requestId);
  const { error } = await supabase.from('absences').update({ status: 'approved' }).eq('id', requestId);
  if (error) {
    console.error('[ABSENCE APPROVE ERROR]', error.code, error.message, error.details);
    // revert
    setRequests(prev => prev.map(r => r.id === requestId ? { ...r, status: 'pending' } : r));
    toast.error('Hyväksyntä epäonnistui');
    return;
  }
  // Lähetä sähköposti + loki notify.ts:n kautta (ei kaadeta hyväksyntää, jos maili epäonnistuu)
  if (emailEnabled && target) {
    try {
      await notifyAbsenceDecision({
        employeeId: target.employeeId,
        status: "approved",
        startDate: target.startDate,
        endDate: target.endDate || null,
        adminMessage: adminResponse?.trim() || undefined,
      });
    } catch (e) {
      console.error("[EMAIL APPROVE ERROR]", e);
      toast.error("Sähköpostin lähetys epäonnistui (hyväksyntä tallessa).");
    }
  }
  toast.success('Poissaolopyyntö hyväksytty');
};


const handleDecline = async (requestId: string) => {
  setRequests(prev => prev.map(r => r.id === requestId ? { ...r, status: 'declined' } : r));

  const target = requests.find(r => r.id === requestId);
  const { error } = await supabase.from('absences').update({ status: 'declined' }).eq('id', requestId);
  if (error) {
    console.error('[ABSENCE DECLINE ERROR]', error.code, error.message, error.details);
    setRequests(prev => prev.map(r => r.id === requestId ? { ...r, status: 'pending' } : r));
    toast.error('Hylkäys epäonnistui');
    return;
  }

  if (emailEnabled && target) {
    try {
      await notifyAbsenceDecision({
        employeeId: target.employeeId,
        status: "declined",
        startDate: target.startDate,
        endDate: target.endDate || null,
        adminMessage: adminResponse?.trim() || undefined,
      });
    } catch (e) {
      console.error("[EMAIL DECLINE ERROR]", e);
      toast.error("Sähköpostin lähetys epäonnistui (hylkäys tallessa).");
    }
  }
  toast.success('Poissaolopyyntö hylätty');
};


  // --- DERIVED LISTS (UI pysyy samana) ---
  const pendingRequests = requests.filter(req => req.status === 'pending');
  const processedRequests = requests.filter(req => req.status !== 'pending');

  const formatDate = (dateString: string) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleDateString('fi-FI', {
      day: 'numeric',
      month: 'numeric',
      year: 'numeric'
    });
  };

  const formatDateTime = (dateString: string) => {
    if (!dateString) return '';
    return new Date(dateString).toLocaleDateString('fi-FI', {
      day: 'numeric',
      month: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="space-y-6">
      {/* Pending Requests */}
      <Card className="shadow-lg border-0 bg-gradient-to-r from-background to-secondary/20">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <AlertTriangle className="w-6 h-6 text-amber-500" />
              <CardTitle className="text-xl text-primary">Odottavat poissaolopyynnöt</CardTitle>
            </div>
            <Badge variant="secondary" className="px-3 py-1">
              {pendingRequests.length} pyyntöä
            </Badge>
          </div>
        </CardHeader>
        <CardContent className="space-y-4">
          {pendingRequests.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <Calendar className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>Ei odottavia poissaolopyyntöjä</p>
            </div>
          ) : (
            pendingRequests.map((request) => (
              <div key={request.id} className="border border-border rounded-lg p-4 bg-background hover:shadow-md transition-shadow">
                <div className="flex items-start justify-between">
                  <div className="flex items-start space-x-3 flex-1">
                    <Avatar className="w-10 h-10">
                      <AvatarFallback className="bg-primary text-primary-foreground">
                        {request.employeeName?.split(' ').map(n => n[0]).join('')}
                      </AvatarFallback>
                    </Avatar>
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <h4 className="font-medium">{request.employeeName}</h4>
                        {getStatusBadge(request.status)}
                      </div>
                      <div className="space-y-1 text-sm text-muted-foreground">
                        <div className="flex items-center gap-2">
                          <Calendar className="w-4 h-4" />
                          <span>
                            {formatDate(request.startDate)} 
                            {request.startDate !== request.endDate && ` - ${formatDate(request.endDate)}`}
                          </span>
                        </div>
                        <div className="flex items-center gap-2">
                          <User className="w-4 h-4" />
                          <span>Syy: {request.reason}</span>
                        </div>
                        <div className="flex items-center gap-2">
                          <Clock className="w-4 h-4" />
                          <span>Jätetty: {formatDateTime(request.submittedAt || '')}</span>
                        </div>
                      </div>
                      {request.message && (
                        <div className="mt-3 p-3 bg-muted/50 rounded-md">
                          <div className="flex items-start gap-2">
                            <MessageSquare className="w-4 h-4 mt-0.5 text-muted-foreground" />
                            <p className="text-sm">{request.message}</p>
                          </div>
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="flex items-center gap-2 ml-4">
                    <Dialog>
                      <DialogTrigger asChild>
                        <div
                          className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 border border-input bg-background hover:bg-accent hover:text-accent-foreground h-9 px-3 cursor-pointer"
                        >
                          <MessageSquare className="w-4 h-4 mr-2" />
                          Vastaa
                        </div>
                      </DialogTrigger>
                      <DialogContent>
                        <DialogHeader>
                          <DialogTitle>Vastaa poissaolopyyntöön</DialogTitle>
                        </DialogHeader>
                        <div className="space-y-4">
                          <div className="space-y-2">
                            <label className="text-sm font-medium">Viesti työntekijälle:</label>
                            <Textarea 
                              value={adminResponse}
                              onChange={(e) => setAdminResponse(e.target.value)}
                              placeholder="Kirjoita viesti..."
                              className="min-h-[100px]"
                            />
                          </div>
                          <div className="flex gap-2">
                            <Button 
                              onClick={() => {
                                handleApprove(request.id);
                                setAdminResponse('');
                              }}
                              className="flex-1"
                            >
                              <CheckCircle className="w-4 h-4 mr-2" />
                              Hyväksy
                            </Button>
                            <Button 
                              variant="destructive"
                              onClick={() => {
                                handleDecline(request.id);
                                setAdminResponse('');
                              }}
                              className="flex-1"
                            >
                              <XCircle className="w-4 h-4 mr-2" />
                              Hylkää
                            </Button>
                          </div>
                        </div>
                      </DialogContent>
                    </Dialog>
                    <Button 
                      size="sm"
                      onClick={() => handleApprove(request.id)}
                      className="bg-green-600 hover:bg-green-700"
                    >
                      <CheckCircle className="w-4 h-4" />
                    </Button>
                    <Button 
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDecline(request.id)}
                    >
                      <XCircle className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              </div>
            ))
          )}
        </CardContent>
      </Card>

      {/* Processed Requests */}
      <Card className="shadow-md">
        <CardHeader className="pb-4">
          <CardTitle className="text-lg">Käsitellyt pyynnöt</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            {processedRequests.map((request) => (
              <div key={request.id} className="flex items-center justify-between p-3 border border-border rounded-md bg-muted/30">
                <div className="flex items-center space-x-3">
                  <Avatar className="w-8 h-8">
                    <AvatarFallback className="text-xs">
                      {request.employeeName?.split(' ').map(n => n[0]).join('')}
                    </AvatarFallback>
                  </Avatar>
                  <div>
                    <p className="font-medium text-sm">{request.employeeName}</p>
                    <p className="text-xs text-muted-foreground">
                      {formatDate(request.startDate)} - {request.reason}
                    </p>
                  </div>
                </div>
                {getStatusBadge(request.status)}
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default AbsenceControlPanel;

"use client";

import React, { useEffect, useMemo, useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { Badge } from "./ui/badge";
import { Avatar, AvatarFallback } from "./ui/avatar";
import { Switch } from "./ui/switch";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "./ui/dialog";
import { Label } from "./ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select";
import {
  Users,
  Plus,
  Edit3,
  Trash2,
  Search,
  UserCheck,
  UserX,
  Mail,
  Building,
} from "lucide-react";
import { toast } from "sonner";
import { supabase } from "@/lib/supaBaseClient";

// Sama tyyppi kuin sulla (shifts jätetään tyhjäksi tässä vaiheessa)
type Employee = {
  id: string;
  name: string;
  email: string;
  department: string;
  isActive: boolean;
  shifts: Array<unknown>;
};

const EmployeeList = () => {
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [loading, setLoading] = useState(true);

  // Hakusuodatus
  const [searchTerm, setSearchTerm] = useState("");

  const [customDepartment, setCustomDepartment] = useState("");
  const [editCustomDepartment, setEditCustomDepartment] = useState("");

  const [creatingNewDept, setCreatingNewDept] = useState(false);
  const [editCreatingNewDept, setEditCreatingNewDept] = useState(false);

  // Edit/Add dialogit
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | null>(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [newEmployee, setNewEmployee] = useState({
    name: "",
    email: "",
    department: "",
    isActive: true,
  });

  // 1) HAKU DB:stä
  useEffect(() => {
    (async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from("employees")
        .select("id, name, email, department, is_active, created_at")
        .order("created_at", { ascending: true });

      if (error) {
        console.error(error);
        toast.error("Työntekijöiden haku epäonnistui");
      } else {
        // Mapataan snake_case -> camelCase

          type EmployeeRow = {
          id: string;
          name: string;
          email: string;
          department: string;
          is_active: boolean;
          created_at: string; // tai Date, jos haluat käsitellä sitä
          };

          const mapped: Employee[] = (data ?? []).map((row: EmployeeRow) => ({
          id: row.id,
          name: row.name,
          email: row.email,
          department: row.department,
          isActive: !!row.is_active,
          shifts: [], // ei vielä käytössä
        }));
        setEmployees(mapped);
      }
      setLoading(false);
    })();
  }, []);

  // 2) LISÄYS
async function handleAddEmployee() {
  const name = newEmployee.name.trim();
  const email = newEmployee.email.trim();
  const dep = (newEmployee.department ?? "").trim();

  if (!name || !email || !dep) {
    toast.error("Täytä kaikki pakolliset kentät");
    return;
  }
  if (dep.toLowerCase() === "uusi osasto") {
    toast.error("Kirjoita osaston nimi.");
    return;
  }

  const { data, error } = await supabase
    .from("employees")
    .insert([{
      name,
      email,
      department: dep,
      is_active: newEmployee.isActive,
    }])
    .select("id, name, email, department, is_active, created_at")
    .single();

    if (error) {
      console.error(error);
      toast.error("Lisäys epäonnistui");
      return;
    }

    const added: Employee = {
      id: data.id,
      name: data.name,
      email: data.email,
      department: data.department,
      isActive: !!data.is_active,
      shifts: [],
    };

    setEmployees((prev) => [...prev, added]);
    setNewEmployee({ name: "", email: "", department: "", isActive: true });
    setIsAddDialogOpen(false);
    toast.success(`${added.name} lisätty`);
      

    await supabase.from("notifications").insert({
      type: "employee_added",
      title: "Uusi työntekijä lisätty",
      message: `${added.name} (${added.department}) lisättiin työntekijälistaan.`,
    });
  }

  // 3) POISTO
  async function handleDeleteEmployee(employeeId: string) {
    const target = employees.find((e) => e.id === employeeId);
    const { error } = await supabase.from("employees").delete().eq("id", employeeId);
    if (error) {
      console.error(error);
      toast.error("Poisto epäonnistui");
      return;
    }
    setEmployees((prev) => prev.filter((e) => e.id !== employeeId));
    toast.success(`${target?.name ?? "Työntekijä"} poistettu`);
  }

  // 4) AKTIIVINEN/EPÄAKTIIVINEN toggle
  async function handleToggleActive(employeeId: string) {
    const current = employees.find((e) => e.id === employeeId);
    if (!current) return;

    const nextActive = !current.isActive;
    // Optimistic update
    setEmployees((prev) =>
      prev.map((e) => (e.id === employeeId ? { ...e, isActive: nextActive } : e))
    );

    const { error } = await supabase
      .from("employees")
      .update({ is_active: nextActive })
      .eq("id", employeeId);

    if (error) {
      console.error(error);
      // Revertoi jos meni pieleen
      setEmployees((prev) =>
        prev.map((e) => (e.id === employeeId ? { ...e, isActive: !nextActive } : e))
      );
      toast.error("Tilan muutos epäonnistui");
      return;
    }

    toast.success(`${current.name} ${nextActive ? "aktivoitu" : "deaktivoitu"}`);
  }

  // 5) EDIT / UPDATE
  function handleEditEmployee(employee: Employee) {
    setSelectedEmployee(employee);
  }

  async function handleUpdateEmployee() {
    if (!selectedEmployee) return;

    const { error } = await supabase
      .from("employees")
      .update({
        name: selectedEmployee.name,
        email: selectedEmployee.email,
        department: selectedEmployee.department,
        is_active: selectedEmployee.isActive,
      })
      .eq("id", selectedEmployee.id);

    if (error) {
      console.error(error);
      toast.error("Päivitys epäonnistui");
      return;
    }

    setEmployees((prev) =>
      prev.map((e) => (e.id === selectedEmployee.id ? selectedEmployee : e))
    );
    setSelectedEmployee(null);
    toast.success("Työntekijätiedot päivitetty");
  }

  // Johdetut arvot (kuten ennen)
  const filteredEmployees = useMemo(() => {
    const q = searchTerm.toLowerCase();
    return employees.filter(
      (e) =>
        e.name.toLowerCase().includes(q) ||
        e.email.toLowerCase().includes(q) ||
        e.department.toLowerCase().includes(q)
    );
  }, [employees, searchTerm]);

  const activeEmployees = employees.filter((e) => e.isActive).length;
const departments = useMemo(
  () =>
    [...new Set(
      employees
        .map(e => (e.department ?? "").trim())
        .filter(v => v && v.toLowerCase() !== "uusi osasto")
    )].sort((a, b) => a.localeCompare(b, "fi")),
  [employees]
);


  // —— UI alla: pidetään sun alkuperäinen rakenne ——
  return (
    <div className="space-y-6">
      <Card className="shadow-lg border-0 bg-gradient-to-r from-background to-secondary/20">
        <CardHeader className="pb-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <Users className="w-6 h-6 text-primary" />
              <CardTitle className="text-xl text-primary">Työntekijähallinta</CardTitle>
            </div>
            <div className="flex items-center gap-2">
              <Badge variant="secondary" className="px-3 py-1">
                <UserCheck className="w-4 h-4 mr-2" />
                {activeEmployees} aktiivista
              </Badge>

              <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
                <DialogTrigger asChild>
                  <div
                    className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 bg-primary text-primary-foreground hover:bg-primary/90 h-10 px-4 py-2 cursor-pointer"
                    onClick={() => setIsAddDialogOpen(true)}
                  >
                    <Plus className="w-4 h-4 mr-2" />
                    Lisää työntekijä
                  </div>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>Lisää uusi työntekijä</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <Label htmlFor="name">Nimi *</Label>
                      <Input
                        id="name"
                        value={newEmployee.name}
                        onChange={(e) =>
                          setNewEmployee((p) => ({ ...p, name: e.target.value }))
                        }
                        placeholder="Etunimi Sukunimi"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="email">Sähköposti *</Label>
                      <Input
                        id="email"
                        type="email"
                        value={newEmployee.email}
                        onChange={(e) =>
                          setNewEmployee((p) => ({ ...p, email: e.target.value }))
                        }
                        placeholder="etunimi.sukunimi@company.com"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label htmlFor="department">Osasto *</Label>
                      <Select
  value={
    creatingNewDept
      ? "NEW_DEPT"
      : (newEmployee.department || "")
  }
  onValueChange={(value) => {
    if (value === "NEW_DEPT") {
      setCreatingNewDept(true);
      setCustomDepartment("");
      setNewEmployee(p => ({ ...p, department: "" })); // puhdas aloitus
    } else {
      setCreatingNewDept(false);
      setCustomDepartment("");
      setNewEmployee(p => ({ ...p, department: value }));
    }
  }}
>
  <SelectTrigger>
    <SelectValue placeholder="Valitse osasto" />
  </SelectTrigger>
  <SelectContent>
    {departments.map((dept) => (
      <SelectItem key={dept} value={dept}>{dept}</SelectItem>
    ))}
    <SelectItem value="NEW_DEPT">+ Uusi osasto…</SelectItem>
  </SelectContent>
</Select>

{creatingNewDept && (
  <Input
    placeholder="Kirjoita uusi osasto…"
    className="mt-2"
    value={customDepartment}
    onChange={(e) => {
      const v = e.target.value;
      setCustomDepartment(v);
      setNewEmployee(p => ({ ...p, department: v })); // päivitetään arvo, mutta ei piiloteta inputtia
    }}
  />
)}



                    </div>
                    <div className="flex items-center space-x-2">
                      <Switch
                        id="active"
                        checked={newEmployee.isActive}
                        onCheckedChange={(checked) =>
                          setNewEmployee((p) => ({ ...p, isActive: checked }))
                        }
                      />
                      <Label htmlFor="active">Aktiivinen työntekijä</Label>
                    </div>
                    <div className="flex gap-2 pt-4">
                      <Button onClick={handleAddEmployee} className="flex-1" disabled={loading}>
                        Lisää työntekijä
                      </Button>
                      <Button
                        variant="outline"
                        onClick={() => setIsAddDialogOpen(false)}
                        className="flex-1"
                      >
                        Peruuta
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </div>
        </CardHeader>

        <CardContent className="space-y-4">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
            <Input
              placeholder="Hae työntekijöitä nimellä, sähköpostilla tai osastolla..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
            />
          </div>

          {/* Employee List */}
          <div className="space-y-3">
            {loading ? (
              <div className="text-center py-8 text-muted-foreground">Ladataan…</div>
            ) : (
              filteredEmployees.map((employee) => (
                <div
                  key={employee.id}
                  className="border border-border rounded-lg p-4 bg-background hover:shadow-md transition-shadow"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-3">
                      <Avatar className="w-12 h-12">
                        <AvatarFallback
                          className={`${
                            employee.isActive
                              ? "bg-primary text-primary-foreground"
                              : "bg-muted text-muted-foreground"
                          }`}
                        >
                          {employee.name
                            .split(" ")
                            .map((n) => n[0])
                            .join("")}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-1">
                          <h4 className="font-medium">{employee.name}</h4>
                          {employee.isActive ? (
                            <Badge
                              variant="outline"
                              className="bg-green-50 text-green-700 border-green-300"
                            >
                              <UserCheck className="w-3 h-3 mr-1" />
                              Aktiivinen
                            </Badge>
                          ) : (
                            <Badge
                              variant="outline"
                              className="bg-gray-50 text-gray-700 border-gray-300"
                            >
                              <UserX className="w-3 h-3 mr-1" />
                              Ei-aktiivinen
                            </Badge>
                          )}
                        </div>
                        <div className="space-y-1 text-sm text-muted-foreground">
                          <div className="flex items-center gap-2">
                            <Mail className="w-4 h-4" />
                            <span>{employee.email}</span>
                          </div>
                          <div className="flex items-center gap-2">
                            <Building className="w-4 h-4" />
                            <span>{employee.department}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      <div className="flex items-center space-x-2">
                        <Label htmlFor={`toggle-${employee.id}`} className="text-sm text-muted-foreground">
                          Aktiivinen
                        </Label>
                        <Switch
                          id={`toggle-${employee.id}`}
                          checked={employee.isActive}
                          onCheckedChange={() => handleToggleActive(employee.id)}
                        />
                      </div>
                      <Button variant="ghost" size="sm" onClick={() => handleEditEmployee(employee)}>
                        <Edit3 className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeleteEmployee(employee.id)}
                        className="text-destructive hover:text-destructive"
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>

          {!loading && filteredEmployees.length === 0 && (
            <div className="text-center py-8 text-muted-foreground">
              <Users className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>Ei työntekijöitä hakukriteereillä</p>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Edit Employee Dialog */}
      <Dialog open={!!selectedEmployee} onOpenChange={() => setSelectedEmployee(null)}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Muokkaa työntekijää</DialogTitle>
          </DialogHeader>
          {selectedEmployee && (
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="edit-name">Nimi</Label>
                <Input
                  id="edit-name"
                  value={selectedEmployee.name}
                  onChange={(e) =>
                    setSelectedEmployee((p) => (p ? { ...p, name: e.target.value } : p))
                  }
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="edit-email">Sähköposti</Label>
                <Input
                  id="edit-email"
                  type="email"
                  value={selectedEmployee.email}
                  onChange={(e) =>
                    setSelectedEmployee((p) => (p ? { ...p, email: e.target.value } : p))
                  }
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="edit-department">Osasto</Label>

<Select
  value={
    editCreatingNewDept
      ? "NEW_DEPT"
      : (selectedEmployee?.department || "")
  }
  onValueChange={(value) => {
    if (!selectedEmployee) return;
    if (value === "NEW_DEPT") {
      setEditCreatingNewDept(true);
      setEditCustomDepartment("");
      setSelectedEmployee(p => p ? { ...p, department: "" } : p);
    } else {
      setEditCreatingNewDept(false);
      setEditCustomDepartment("");
      setSelectedEmployee(p => p ? { ...p, department: value } : p);
    }
  }}
>
  <SelectTrigger>
    <SelectValue placeholder="Valitse osasto" />
  </SelectTrigger>
  <SelectContent>
    {departments.map((dept) => (
      <SelectItem key={dept} value={dept}>{dept}</SelectItem>
    ))}
    <SelectItem value="NEW_DEPT">+ Uusi osasto…</SelectItem>
  </SelectContent>
</Select>

{editCreatingNewDept && (
  <Input
    placeholder="Kirjoita uusi osasto…"
    className="mt-2"
    value={editCustomDepartment}
    onChange={(e) => {
      const v = e.target.value;
      setEditCustomDepartment(v);
      setSelectedEmployee(p => p ? { ...p, department: v } : p);
    }}
  />
)}


              </div>
              <div className="flex items-center space-x-2">
                <Switch
                  id="edit-active"
                  checked={selectedEmployee.isActive}
                  onCheckedChange={(checked) =>
                    setSelectedEmployee((p) => (p ? { ...p, isActive: checked } : p))
                  }
                />
                <Label htmlFor="edit-active">Aktiivinen työntekijä</Label>
              </div>
              <div className="flex gap-2 pt-4">
                <Button onClick={handleUpdateEmployee} className="flex-1">
                  Tallenna muutokset
                </Button>
                <Button variant="outline" onClick={() => setSelectedEmployee(null)} className="flex-1">
                  Peruuta
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>

      {/* Department Statistics */}
      <Card className="shadow-md">
        <CardHeader>
          <CardTitle className="text-lg">Osastotilastot</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {useMemo(() => {
              const depts = [...new Set(employees.map((e) => e.department))];
              return depts.map((department) => {
                const deptEmployees = employees.filter((e) => e.department === department);
                const activeDeptEmployees = deptEmployees.filter((e) => e.isActive);
                return (
                  <div key={department} className="text-center p-3 border border-border rounded-lg">
                    <h4 className="font-medium mb-2">{department}</h4>
                    <div className="text-2xl font-bold text-primary">{activeDeptEmployees.length}</div>
                    <div className="text-xs text-muted-foreground">{deptEmployees.length} yhteensä</div>
                  </div>
                );
              });
            }, [employees])}
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default EmployeeList;

"use client";

import React, { useEffect, useMemo, useState } from "react";
import { Badge } from "./ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "./ui/card";
import { Calendar, Clock, Users, AlertCircle, Lock, Plane, Plus, Filter } from "lucide-react";
import { ShiftType, Employee, DateInfo } from "../types";
import { supabase } from "@/lib/supaBaseClient";
import { toast } from "sonner";
import { Popover, PopoverContent, PopoverTrigger } from "./ui/popover";
import { Button } from "./ui/button";
import { Input } from "./ui/input";
import { useScheduleStore } from "@/store/useScheduleStore";
import { useSettingsStore } from "@/store/useSettingsStore";
import { alignToWeekStart } from "@/lib/dateUtils";




type DateCell = DateInfo & { iso: string };

        type EmployeeRow = {
          id: string;
          name: string;
          email: string;
          department: string;
          is_active: boolean;
        };

        type AbsenceRow = {
  employee_id: string;
  start_date: string;
  end_date: string | null;
  reason: string | null;
  status: "pending" | "approved" | "declined";
};



interface ScheduleTableProps {
  employees?: Employee[]; // säilytetään signatuuri
}


function addDaysISO(iso: string, add: number) {
  // "T00:00:00" poistaa aikavyöhykkeen heiton
  const d = new Date(iso + "T00:00:00Z");
  d.setUTCDate(d.getUTCDate() + add);
  return d.toISOString().slice(0, 10);
}

function fiWeekdayShort(d: Date) {
  // su-to klo 0 locale -> FI näyttää ma, ti, ke...
  return d
    .toLocaleDateString("fi-FI", { weekday: "short" })
    .replace(".", "")
    .toUpperCase()
    .slice(0, 2);
}

 function fiDayMonth(d: Date) {
   const day = d.getDate();
   const month = d.getMonth() + 1;
   return `${day}.${month}`;
 }

// 🆕 FI-normalisointi (lowercase + diakriittien poisto)
function normalizeFi(s: string) {
  return (s || "")
    .toLowerCase()
    .normalize("NFD")
    .replace(/[\u0300-\u036f]/g, "");
}

 const DEFAULT_FILTERS = {
  departments: [] as string[],
  showActive: false,
  showInactive: false,
  searchTerm: "",
}


const ScheduleTable: React.FC<ScheduleTableProps> = () => {

const startISO = useScheduleStore((s) => s.startDateISO);
const days = useScheduleStore((s) => s.days);
const weekStartDay = useSettingsStore((s) => s.settings.general.weekStartDay);

const alignedStart = useMemo(
  () => alignToWeekStart(startISO, weekStartDay),
  [startISO, weekStartDay]
);



const shiftsMap = useScheduleStore((s) => s.shiftsMap) ?? {};
const filters = useScheduleStore((s) => s.filters) ?? DEFAULT_FILTERS;

//tila-filtteri on aktiivinen, jos vain toinen on päällä
const stateFilterActive = filters.showActive !== filters.showInactive;

const employees = useScheduleStore(s => s.employees);
const filteredEmployees = useMemo(() => {
  const term = normalizeFi((filters.searchTerm ?? "").trim());
  return employees.filter((emp) => {
    // department
    if (filters.departments.length > 0 && !filters.departments.includes(emp.department)) {
      return false;
    }
    // active/inactive XOR
    if (stateFilterActive) {
      if (filters.showActive && !emp.isActive) return false;
      if (filters.showInactive && emp.isActive) return false;
    }
    // search (nimi + email + osasto)
    if (term.length > 0) {
      const hay =
        normalizeFi(emp.name) +
        " " +
        normalizeFi(emp.email) +
        " " +
        normalizeFi(emp.department);
      if (!hay.includes(term)) return false;
    }
    return true;
  });
}, [employees, filters, stateFilterActive]);
  
  const [loading, setLoading] = useState(true);
  const [selectedCell, setSelectedCell] = useState<{ employee: string; day: number } | null>(null);
  const [openPopover, setOpenPopover] = useState<string | null>(null);




  // Päivärivi tuotetaan ISO:sta -> näyttää täsmälleen sun UI:n kaltaisen otsikon
const dates: DateCell[] = useMemo(() => {
  return Array.from({ length: days }).map((_, i): DateCell => {
    const iso = addDaysISO(alignedStart, i);
    const d = new Date(iso + "T00:00:00Z");
    return { day: fiWeekdayShort(d), date: fiDayMonth(d), iso };
  });
}, [alignedStart, days]);

  // Alignaa heti mountissa ja aina kun viikon aloituspäivä muuttuu,
  // jotta näkymä on johdonmukainen myös ilman Toolbarin efektiä.


  // Vuorot mapattuna: key = `${employee_id}|${work_date}`
  

  // 1) Hae työntekijät + 2) hae vuorot valitulle jaksolle
  useEffect(() => {
    (async () => {
      try {
        setLoading(true);

        // Employees
        const { data: empData, error: empErr } = await supabase
          .from("employees")
          .select("id, name, email, department, is_active, created_at")
          .order("created_at", { ascending: true });

        if (empErr) throw empErr;

const mappedEmp: Employee[] = (empData ?? []).map((row: EmployeeRow) => ({
  id: row.id,
  name: row.name,
  email: row.email,               // ✅ lisää email
  department: row.department,
  isActive: !!row.is_active,
  shifts: [] as ShiftType[],      // ✅ lisää shifts placeholder
}));


        const { data: s, error: sErr } = await supabase
  .from("shifts")
  .select("employee_id, work_date, type, hours")
  .gte("work_date", dates[0].iso)
  .lte("work_date", dates[dates.length - 1].iso)
  .in(
    "employee_id",
    mappedEmp.map((e) => e.id)
  );

if (sErr) throw sErr;

useScheduleStore.getState().hydrate({
  employees: mappedEmp,
  dates,
  shifts: (s ?? []).map(r => ({
    employee_id: r.employee_id,
    work_date: r.work_date,
    type: r.type as "normal" | "locked" | "absent" | "holiday",
    hours: r.hours ?? 0,
  })),
});
const { data: abs, error: absErr } = await supabase
  .from("absences")
  .select("employee_id, start_date, end_date, reason, status")
  .eq("status", "approved")
  .in("employee_id", mappedEmp.map((e) => e.id));

if (absErr) throw absErr;

const absMap: Record<string, { type: "absent" | "holiday"; reason: string }> = {};
(abs ?? []).forEach((a: AbsenceRow) => {
  const start = new Date(a.start_date);
  const end = a.end_date ? new Date(a.end_date) : start;

  for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
    const iso = d.toISOString().slice(0, 10);
    absMap[`${a.employee_id}|${iso}`] = {
      type: a.reason?.toLowerCase() === "holiday" ? "holiday" : "absent",
      reason: a.reason ?? "",
    };
  }
});
setAbsencesMap(absMap);

      } catch (e) {
        console.error(e);
        toast.error("Tietojen haku epäonnistui");
      } finally {
        setLoading(false);
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [startISO, days]);



const [absencesMap, setAbsencesMap] = useState<Record<string, { type: "absent" | "holiday"; reason: string }>>({});

  // Lue solun vuoro mapista
function getShift(empId: string, dayIndex: number): ShiftType {
  const iso = dates[dayIndex].iso;
  const key = `${empId}|${iso}`;
  const row = shiftsMap[key];
  if (!row) return { type: "empty" };                 // UI-fallback
  if (row.type === "normal" || row.type === "locked") {
    return { type: row.type, hours: row.hours ?? 0 };
  }
  return { type: row.type }; // absent/holiday
}

  // Yhteensä tunnit / työntekijä
  const getTotalHours = (employee: Employee) =>
    dates.reduce((sum, _, i) => {
      const s = getShift(employee.id, i);
      return sum + (s.hours || 0);
    }, 0);

  // Klikkaus: toggle empty <-> normal(8h), upsert DB:hen
// ScheduleTable.tsx

const applyCellChange = useScheduleStore(s => s.applyCellChange);

function handleCellClick(employeeId: string, dayIndex: number, hours: number | null) {
  const iso = dates[dayIndex].iso;
  applyCellChange({
    employee_id: employeeId,
    work_date: iso,
    hours
  });
  setSelectedCell({ employee: employeeId, day: dayIndex });
}




  // UI-helper solun ulkoasuun
  const getShiftDisplay = (shift: ShiftType) => {
    switch (shift.type) {
      case "normal":
        return { content: `${shift.hours}h`, color: "bg-primary text-primary-foreground", icon: <Clock className="w-3 h-3" /> };
      case "locked":
        return { content: `${shift.hours}h`, color: "bg-amber-500 text-white", icon: <Lock className="w-3 h-3" /> };
      case "absent":
        return { content: "A", color: "bg-destructive text-destructive-foreground", icon: <AlertCircle className="w-3 h-3" /> };
      case "holiday":
        return { content: "H", color: "bg-blue-500 text-white", icon: <Plane className="w-3 h-3" /> };
      default:
        return { content: "", color: "bg-muted hover:bg-accent", icon: <Plus className="w-3 h-3 opacity-0 group-hover:opacity-50" /> };
    }
  };

  if (loading) {
    return <div className="text-center py-8 text-muted-foreground">Ladataan…</div>;
  }

  return (
  <div className="w-full space-y-6">
    <Card className="shadow-lg border-0 bg-gradient-to-r from-background to-secondary/20">
      <CardHeader className="pb-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <Calendar className="w-6 h-6 text-primary" />
            <CardTitle className="text-2xl text-primary">Vuorot</CardTitle>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="secondary" className="px-3 py-1">
            <Users className="w-4 h-4 mr-2" />
            {filteredEmployees.length} työntekijää
            </Badge>
            {(filters.departments.length > 0 || stateFilterActive) && (
              <Badge variant="outline" className="px-3 py-1">
                <Filter className="w-3 h-3 mr-1" />
                Suodatettu
              </Badge>
              )}
              </div>
          </div>

      </CardHeader>
  {(filters.departments.length > 0 || stateFilterActive || (filters.searchTerm ?? "").trim()) && (
    <div className="px-6 pb-2">
      <div className="text-sm text-muted-foreground bg-accent/40 px-3 py-2 rounded-md inline-flex items-center gap-3 flex-wrap">
        <Filter className="w-4 h-4" />
        <div className="flex flex-wrap gap-x-3 gap-y-1">
          {stateFilterActive && (
            <span>{filters.showActive ? "Vain aktiiviset työntekijät" : "Vain ei-aktiiviset työntekijät"}</span>
          )}
          {filters.departments.length > 0 && (
            <span>Osastot: {filters.departments.join(", ")}</span>
          )}
          {(filters.searchTerm ?? "").trim() && (
            <span>Haku: “{filters.searchTerm.trim()}”</span>
            )}
        </div>
      </div>
    </div>
  )}


      <CardContent className="p-0">
        <div className="overflow-x-auto">
          <div className="min-w-full">
            {/* Header */}
            <div className="bg-muted/50 border-b">
              <div
              className="grid gap-px"
              style={{ gridTemplateColumns: `minmax(200px,280px) repeat(${days}, minmax(96px, 1fr))` }}
              >
                <div className="p-4 bg-background">
                  <span className="text-sm font-medium text-muted-foreground">Työntekijä</span>
                </div>
                {dates.map((date, index) => (
                  <div key={index} className="p-3 bg-background text-center">
                    <div className="text-xs font-medium text-muted-foreground">{date.day}</div>
                    <div className="text-sm font-semibold mt-1">{date.date}</div>
                  </div>
                ))}
              </div>
            </div>

            {/* Employee Rows */}
              <div className="divide-y divide-border">
              {filteredEmployees.length === 0 ? (
                <div className="p-12 text-center text-muted-foreground">
                  <Filter className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>Ei työntekijöitä näytettäväksi nykyisillä suodattimilla</p>
                  <p className="text-sm mt-1">Muuta suodattimia nähdäksesi työntekijöitä</p>
                </div>
              ) : filteredEmployees.map((employee) => (

                <div
                  key={employee.id}
                  className="grid gap-px hover:bg-accent/30 transition-colors"
                  style={{ gridTemplateColumns: `minmax(200px,280px) repeat(${days}, minmax(96px, 1fr))` }}
                >
                  <div className="p-4 bg-background flex items-center justify-between">
                    <div>
                      <div className="flex items-center gap-2">
                        <span className="font-medium">{employee.name}</span>
                        {!employee.isActive && (
                          <Badge variant="destructive" className="text-xs">Ei-aktiivinen</Badge>
                        )}
                      </div>
                      <div className="text-xs text-muted-foreground">{employee.department}</div>
                    </div>
                    <Badge variant="outline" className="text-xs">
                      {getTotalHours(employee)}h
                    </Badge>
                  </div>

{dates.map((_, dayIndex) => {
  const shift = getShift(employee.id, dayIndex);
  const shiftDisplay = getShiftDisplay(shift);
  const isSelected =
    selectedCell?.employee === employee.id && selectedCell?.day === dayIndex;

  const key = `${employee.id}|${dates[dayIndex].iso}`;
  const absence = absencesMap[key];

  return (
    <Popover
  key={dayIndex}
  open={openPopover === `${employee.id}-${dayIndex}`}
  onOpenChange={(o) =>
    setOpenPopover(o ? `${employee.id}-${dayIndex}` : null)
  }
>
  <PopoverTrigger asChild>
    <div
      className={`
        h-16 p-2 m-0 rounded-none border-0 group
        flex items-center justify-center
        ${isSelected ? "ring-2 ring-ring ring-offset-2" : ""}
        transition-all duration-200 hover:scale-105 hover:shadow-md
        ${
          absence
            ? absence.type === "holiday"
              ? "bg-blue-100 cursor-not-allowed"
              : "bg-red-100 cursor-not-allowed"
            : shiftDisplay.color + " cursor-pointer"
        }
      `}
    >
      <div className="flex flex-col items-center space-y-1">
        {absence ? (
          <>
            <span
              className={`text-xs font-medium ${
                absence.type === "holiday" ? "text-blue-600" : "text-red-600"
              }`}
            >
              {absence.type === "holiday" ? "L" : "A"}
            </span>
            <span className="text-[10px]">
              {absence.type === "holiday" ? "Loma" : "Poissaolo"}
            </span>
          </>
        ) : (
          <>
            {shiftDisplay.icon}
            {shiftDisplay.content && (
              <span className="text-xs font-medium">
                {shiftDisplay.content}
              </span>
            )}
          </>
        )}
      </div>
    </div>
  </PopoverTrigger>

  {/* Näytä PopoverContent vain jos ei ole absence */}
{!absence && (
  <PopoverContent className="w-64 p-3 space-y-3" side="bottom" align="center">
    <div className="text-sm font-medium text-center">
      {employee.name} – {dates[dayIndex].day} {dates[dayIndex].date}
    </div>

    {/* Pikavalinnat */}
    <div className="grid grid-cols-2 gap-2">
      {[4, 6, 7.5, 8].map((h) => (
        <Button
          key={h}
          variant="outline"
          size="sm"
          onClick={() => {
            handleCellClick(employee.id, dayIndex, h);
            setOpenPopover(null);
          }}
          className="justify-center"
        >
          {h}h
        </Button>
      ))}
    </div>

    {/* Muu-arvo + Tallenna */}
    <div className="flex items-center gap-2">
      <Input
        type="number"
        step="0.5"
        placeholder="esim. 5.5"
        className="h-8"
        onKeyDown={(e: React.KeyboardEvent<HTMLInputElement>) => {
          if (e.key === "Enter") {
            const val = parseFloat(e.currentTarget.value);
            if (!isNaN(val)) {
              handleCellClick(employee.id, dayIndex, val);
              setOpenPopover(null);
            }
          }
        }}
      />
      <Button
        size="sm"
        onClick={(e) => {
          const input = e.currentTarget.parentElement?.querySelector("input") as HTMLInputElement | null;
          const val = input ? parseFloat(input.value) : NaN;
          if (!isNaN(val)) {
            handleCellClick(employee.id, dayIndex, val);
            setOpenPopover(null);
          }
        }}
      >
        ✓
      </Button>
    </div>

    {/* Poista vuoro */}
    <div className="flex justify-center">
      <Button
        variant="ghost"
        size="sm"
        onClick={() => {
          handleCellClick(employee.id, dayIndex, 0);
          setOpenPopover(null);
        }}
        className="text-destructive"
      >
        Poista (0h)
      </Button>
    </div>

    <div className="text-xs text-muted-foreground text-center">
      Vinkki: 0h poistaa vuoron.
    </div>
  </PopoverContent>
)}

</Popover>

  );
})}

                </div>
              ))}
            </div>

            {/* Summary Row */}
            <div className="bg-accent/50 border-t-2 border-primary/20">
              <div
              className="grid gap-px"
              style={{ gridTemplateColumns: `minmax(200px,280px) repeat(${days}, minmax(96px, 1fr))` }}
              >

                <div className="p-4 bg-background">
                  <div className="flex items-center space-x-2">
                    <Users className="w-4 h-4 text-muted-foreground" />
                    <span className="font-medium text-sm">
                      Yhteensä ({filteredEmployees.length} työntekijää)
                    </span>
                  </div>
                </div>
                {dates.map((_, dayIndex) => {
                  const dayTotal = filteredEmployees.reduce((total, emp) => {
                    const s = getShift(emp.id, dayIndex);
                    return total + (s?.hours || 0);
                  }, 0);

                  const filledCount = filteredEmployees.filter(
                    (emp) => getShift(emp.id, dayIndex)?.type !== "empty"
                  ).length;

                  return (
                    <div key={dayIndex} className="p-3 bg-background text-center">
                      <div className="text-sm font-semibold text-primary">{dayTotal}h</div>
                      <div className="text-xs text-muted-foreground">{filledCount} henkilöä</div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>

    {/* Legend */}
    <Card className="shadow-md">
      <CardContent className="p-4">
        <div className="flex flex-wrap gap-4 items-center justify-center">
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-primary rounded-sm flex items-center justify-center">
              <Clock className="w-2.5 h-2.5 text-primary-foreground" />
            </div>
            <span className="text-sm">Normaali vuoro</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-amber-500 rounded-sm flex items-center justify-center">
              <Lock className="w-2.5 h-2.5 text-white" />
            </div>
            <span className="text-sm">Lukittu vuoro</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-destructive rounded-sm flex items-center justify-center">
              <AlertCircle className="w-2.5 h-2.5 text-destructive-foreground" />
            </div>
            <span className="text-sm">Poissaolo</span>
          </div>
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-blue-500 rounded-sm flex items-center justify-center">
              <Plane className="w-2.5 h-2.5 text-white" />
            </div>
            <span className="text-sm">Loma</span>
          </div>
        </div>
      </CardContent>
    </Card>

    {/* Footer Note */}
    <div className="text-center text-xs text-muted-foreground bg-muted/30 p-3 rounded-lg">
      Vinkki: klikkaa solua → valitse tunnit. 0h poistaa vuoron.
    </div>
  </div>
);
}

export default ScheduleTable;

// components/SettingsDialog.tsx
"use client";

import { useState } from "react";
import { toast } from "sonner";
import { Popover, PopoverContent, PopoverTrigger } from "./ui/popover";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "./ui/tabs";
import { ScrollArea } from "./ui/scroll-area";
import { Separator } from "./ui/separator";
import { Button } from "./ui/button";
import { Switch } from "./ui/switch";
import { Slider } from "./ui/slider";
import { Label } from "./ui/label";
import { Input } from "./ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/select";
import {
  Settings as SettingsIcon, Download, Upload, RotateCcw, Paintbrush, Zap, FileSpreadsheet,
  Bell, Shield, Sun, Moon, Monitor, Globe, HardDrive, Database, Trash2
} from "lucide-react";

 import { useSettingsStore } from "@/store/useSettingsStore";
 import { saveNotificationSettingsToDb } from "@/lib/settingsDb";
 import { applyTheme } from "@/lib/theme";
import type {
  Theme, Language, WeekStartDay, DateFormat
} from "@/lib/settingsSchema";

export default function SettingsDialog() {
  const [isOpen, setIsOpen] = useState(false);

  const settings = useSettingsStore((s) => s.settings);
  const updateGeneralSettings = useSettingsStore((s) => s.updateGeneral);
  const updateAutoGenerationSettings = useSettingsStore((s) => s.updateAutoGen);
  const updateExportSettings = useSettingsStore((s) => s.updateExport);
  const updateNotificationSettings = useSettingsStore((s) => s.updateNotifications);
  const updateSystemSettings = useSettingsStore((s) => s.updateSystem);
  const resetSettings = useSettingsStore((s) => s.reset);
  const importFromJson = useSettingsStore((s) => s.importFromJson);
  const exportToFile = useSettingsStore((s) => s.exportToFile);

  const handleExportSettings = () => exportToFile();

  const handleImportSettings = () => {
    const input = document.createElement("input");
    input.type = "file";
    input.accept = "application/json";
    input.onchange = async () => {
      if (!input.files?.[0]) return;
      try {
        const text = await input.files[0].text();
        const json = JSON.parse(text);
        const res = importFromJson(json);
        if (!res.ok) {
          toast.error(`Virhe tiedostossa: ${res.error}`);
          return;
        }
        toast.success("Asetukset tuotu");
      } catch {
        toast.error("Tiedoston luku epäonnistui");
      }
    };
    input.click();
  };

  const handleResetSettings = () => {
    resetSettings();
    toast.success("Asetukset palautettu oletuksiin");
  };

  return (
    <Popover open={isOpen} onOpenChange={setIsOpen}>
      <PopoverTrigger asChild>
        <div
          className="inline-flex items-center justify-center whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 hover:bg-accent hover:text-accent-foreground h-9 w-9 cursor-pointer"
          onClick={() => setIsOpen(!isOpen)}
          aria-label="Asetukset"
        >
          <SettingsIcon className="w-4 h-4" />
        </div>
      </PopoverTrigger>

      <PopoverContent className="w-[680px] p-0" align="end" sideOffset={5}>
        {/* Header */}
        <div className="p-4 border-b border-border">
          <div className="flex items-center justify-between">
            <h3 className="font-medium">Asetukset</h3>
            <div className="flex items-center gap-2">
              <Button variant="ghost" size="sm" onClick={handleExportSettings} className="h-6 px-2 text-xs">
                <Download className="w-3 h-3 mr-1" /> Vie
              </Button>
              <Button variant="ghost" size="sm" onClick={handleImportSettings} className="h-6 px-2 text-xs">
                <Upload className="w-3 h-3 mr-1" /> Tuo
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={handleResetSettings}
                className="h-6 px-2 text-xs text-destructive hover:text-destructive"
              >
                <RotateCcw className="w-3 h-3 mr-1" /> Palauta
              </Button>
            </div>
          </div>
        </div>

        {/* Body */}
        <ScrollArea className="max-h-[600px]">
          <Tabs defaultValue="general" className="w-full">
            <TabsList className="grid w-full grid-cols-5 p-1 m-2">
              <TabsTrigger value="general" className="text-xs">
                <Paintbrush className="w-3 h-3 mr-1" /> Yleiset
